import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';

let AddList = (props) => {
  const [state, setstate] = React.useState(false);
  const [showUpdateBox, setShow] = React.useState(false);

  let changestate = () => {
    setstate(!state);
  }


  let performUpdate = (index) => {
    let editEles = document.getElementsByClassName('editBtn');
    for (let i = 0; i < editEles.length; i++) {
      console.log(i,index)
      if(i!==index)
      editEles[i].disabled = true;
    }
    setShow(!showUpdateBox);
  }

  return (
    <React.Fragment>

      <td><button id="complete" onClick={changestate}>
        {state ? <i className="material-icons" style={{ fontSize: "36px" }}>check_box</i> : <i className="material-icons" style={{ fontSize: "24px" }}>check_box_outline_blank</i>}
      </button>
      </td>

      <td className={state ? "strike" : "nostrike"}><span className="fontli">&nbsp;{props.listEle}</span></td>

      <td><input type="text" onInput={props.editRow()} className={showUpdateBox ? "showDisplay" : "noDisplay"} />
        <button class="editBtn"><i className='fas fa-edit' onClick={() => performUpdate(props.index)} style={{ fontSize: '36px', cursor: 'pointer' }}></i></button>
      </td>

      <td><i className="material-icons" onClick={() => props.deleteRow()} style={{ fontSize: "36px", color: "red", cursor: "pointer" }}>delete</i></td>
    </React.Fragment>

  )
}



let TodoAppComponent = () => {
  const [todoArr, setTodo] = React.useState(['Shopping', 'React ToDo'])

  let printVal = () => {
    let inpEle = document.getElementById('task');
    let newVal = inpEle.value;
    if (inpEle.value == "") return;
    inpEle.value = "";
    inpEle.focus();
    let newtodoArr = todoArr.concat([newVal]);
    setTodo(newtodoArr);
  }

  let deleteRow = (todoindex) => {
    let newTodoArr = [...todoArr];
    newTodoArr.splice(todoindex, 1);
    setTodo(newTodoArr);
  }

  let editRow = (todoIndex) => {
    let ele = document.getElementsByClassName('showDisplay')[0];
    if (!ele) return;
    let newtodoArr = [...todoArr];
    let updatedVal = ele.value;
    newtodoArr.splice(todoIndex, 1, updatedVal);
    setTodo(newtodoArr);

  }

  return (
    <div style={{ marginTop: "5%" }}>
      <h1>ToDo App</h1>
      <input type="text" id="task" />
      <i id="addBtn" onClick={printVal} className="fa fa-plus-square" style={{ fontSize: "34px", cursor: "pointer" }}></i><br /><br />
      <table className="table table-hover">
        <tbody>
          {todoArr.map((todo, index) => <tr><AddList listEle={todo} todoIndex={index} deleteRow={() => deleteRow(index)}
            editRow={() => editRow(index)}></AddList></tr>)}
        </tbody>
      </table>


    </div>
  )
}


ReactDOM.render(
  <React.StrictMode>

    <TodoAppComponent />
  </React.StrictMode>,
  document.getElementById('root')
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
