import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';

let AddList = (props) => {
  return (
    <ul>
      <li>{props.listEle}
      <button id>Completed</button>
      </li>
    </ul>
  )
}

let TodoAppComponent = () => {
  const [todoArr, setTodo] = React.useState([{ name: 'Shopping', isCompleted: false }, { name: 'React ToDo', isCompleted: false }])

  let printVal = () => {
    let inpEle = document.getElementById('task');
    let newVal = { name: inpEle.value, isCompleted: false };
    inpEle.value = "";
    inpEle.focus();
    let newtodoArr = todoArr.concat([newVal]);
    setTodo(newtodoArr);
  }

  return (
    <div style={{ marginLeft: "43%", marginTop: "5%" }}>
      <input type="text" id="task" />
      <button onClick={printVal}>Add ToDo</button>

      {todoArr.map((todo) => <AddList listEle={todo.name} />)}
    </div>
  )
}


ReactDOM.render(
  <React.StrictMode>
    <TodoAppComponent />
  </React.StrictMode>,
  document.getElementById('root')
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
