import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';

let AddList = (props) => {
  const [state, setstate] = React.useState(false);

  let changestate = () => {
    setstate(!state);
  }

  return (
    <React.Fragment>
      <td><button id="complete" style={{ marginLeft: "5%" }} onClick={changestate}>
        {state ? <i className="fa fa-check-square-o" style={{ fontSize: "24px" }}></i> : <i class="material-icons" style={{ fontSize: "24px" }}>check_box_outline_blank</i>}
      </button>
      </td>
      <td className={state ? "strike" : "nostrike"}><span className="fontli">&nbsp;{props.listEle}</span></td>
      <td><i class="material-icons" style={{ fontSize: "40px", color: "red", textAlign: "right",cursor:"pointer" }}>delete</i></td>

    </React.Fragment>

  )
}

let TodoAppComponent = () => {
  const [todoArr, setTodo] = React.useState(['Shopping', 'React ToDo'])

  let printVal = () => {
    let inpEle = document.getElementById('task');
    let newVal = inpEle.value;
    if (inpEle.value == "") return;
    inpEle.value = "";
    inpEle.focus();
    let newtodoArr = todoArr.concat([newVal]);
    setTodo(newtodoArr);
  }

  return (
    <div style={{ marginLeft: "30%", marginTop: "5%" }}>
      <h1>ToDo App</h1>
      <input type="text" id="task" />
      <i id="addBtn" onClick={printVal} class="fa fa-plus-square" style={{ fontSize: "34px", cursor: "pointer" }}></i><br /><br />
      <table className="table table-hover">
        <tbody>
        {todoArr.map((todo) => <tr><AddList listEle={todo}></AddList></tr>)}
        </tbody>
      </table>


    </div>
  )
}


ReactDOM.render(
  <React.StrictMode>

    <TodoAppComponent />
  </React.StrictMode>,
  document.getElementById('root')
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
