import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';

let AddList = (props) => {
  const [state, setstate] = React.useState(false);

  let changestate = () => {
    setstate(!state);
  }

  return (
    <React.Fragment>
      <td><button id="complete" style={{ marginLeft: "5%" }} onClick={changestate}>
        {state ? <i className="fa fa-check-square-o" style={{ fontSize: "24px" }}></i> : <i class="material-icons" style="font-size:36px">replay</i>}
        </button>
      </td>
      <td className={state ? "strike" : "nostrike"}><span className="fontli">{props.listEle}</span></td>

    </React.Fragment>

  )
}

let TodoAppComponent = () => {
  const [todoArr, setTodo] = React.useState(['Shopping', 'React ToDo'])

  let printVal = () => {
    let inpEle = document.getElementById('task');
    let newVal = inpEle.value;
    if (inpEle.value == "") return;
    inpEle.value = "";
    inpEle.focus();
    let newtodoArr = todoArr.concat([newVal]);
    setTodo(newtodoArr);
  }

  return (
    <div style={{ marginLeft: "30%", marginTop: "5%" }}>

      <input type="text" id="task" />
      <button id="addBtn" onClick={printVal}>Add ToDo</button>
      <table>
        {todoArr.map((todo) => <tr><AddList listEle={todo}></AddList></tr>)}
      </table>


    </div>
  )
}


ReactDOM.render(
  <React.StrictMode>

    <TodoAppComponent />
  </React.StrictMode>,
  document.getElementById('root')
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
